sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    //Sends an on-call push notification to a user allowing them to accept or reject assignment. Must have ITSM Mobile Agent plugin installed first, then log into mobile app for your instance.

	//If you run into any issues, check the on_call_push_request table and ensure the acceptor and document_id fields are properly filled in. If not, double check your username and incident numbers.

    (function() {

        try {

			var acceptor = request.body.data.acceptor.toString();
			var incident = request.body.data.number.toString();

			var userGr = new GlideRecord('sys_user');
			userGr.get('user_name', acceptor);
			var userSysID = userGr.sys_id;

			var incidentGr = new GlideRecord('incident');
			incidentGr.get('number', incident);
			var incidentSysID = incidentGr.sys_id;

            var gr = new GlideRecord('on_call_push_request');
            gr.newRecord();
            gr.setValue('acceptor', userSysID);
            gr.setValue('state', 'requested');
            gr.setValue('source_table', 'incident');
            gr.setValue('document_id', incidentSysID);
            gr.setValue('active', true);
            gr.setValue('iteration', '1');
            gr.insert();

            var resBody = {};
            resBody.information = "Mobile push successfully created.";
            response.setBody(resBody);

        } catch (ex) {
            var message = ex.getMessage();
            gs.error(message);
        }

    })();

})(request, response);
