sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var slackEndpoint = request.body.data.endpoint;
    var incidentNum = request.body.data.incident;

    var incident = new global.GlideQuery('incident')
        .where('number', incidentNum)
        .selectOne('assigned_to.user_name', 'assigned_to$DISPLAY', 'assigned_to.sys_id')
        .get();

    var assignee = incident.assigned_to$DISPLAY.toString();
    var username = incident.assigned_to.user_name.toString();
    var incLink = 'https://' + gs.getProperty('instance_name') + '.service-now.com/incident.do?sys_id=' + incident.sys_id;
    var userLink = 'https://' + gs.getProperty('instance_name') + '.service-now.com/sys_user.do?sys_id=' + incident.assigned_to.sys_id;

    var r = new sn_ws.RESTMessageV2('Slack', 'POST');

    r.setRequestBody('{"text":"Incident <' + incLink + '|' + incidentNum + '> is assigned to: ' + assignee + ' (<' + userLink + '|' + username + '>)"}');

    r.setStringParameter('endpoint', slackEndpoint);
    r.execute();

})(request, response);
